import React from 'react'; 
import ChartBar from './ChartBar'; 
import './Chart.css'; 

const Chart = (props) => {
    const dataPointValues = props.dataPoints.map(dataPoint => dataPoint.value);// to return all of the values of data points into numerical values, since the map will correlate the dat points to the value property 
const totalMaximum = Math.max(...dataPointValues); // using the ... (srepad) operator to pull out the values of the array dataPointValues, since the max function can only take lists 

    return (
    <div className = "chart">
        {props.dataPoints.map((dataPoint )=> (
        <ChartBar 
        key = {dataPoint.lable}
        values= {dataPoint.values}
        maxValues = {totalMaximum}
        label ={dataPoint.lable}
        />
        ))}
         </div> 
    );
};

export default Chart;
