import React, { useState } from 'react';
import './ExpenseForm.css';


const ExpenseForm = () => {

    const [enteredTitle, setEnteredTitle] = useState('');
    const [enteredAmount, setEnteredAmount] = useState('');
    const [enteredDate, setEnteredDate] = useState('');

    const titleChangeHandler = (event) => {
        setEnteredTitle(event.target.value);
    };
    const amountChangeHandler = (event) => {
        setEnteredAmount(event.target.value);
    };
    const dateChangeHandler = (event) => {
        setEnteredDate(event.target.value);
    };
    const submitHandler = (event) => {
        event.preventDefault(); //used to prevent the code to be sent, thus the page will not reload whiout sending any new request to the browser, not JSX code, only JS

        const expenseData = { // expense data is an object with three propperties that point to the prop of state
            title: enteredTitle,
            amount: enteredAmount,
            date: new Date(enteredDate) //creates a new date with the date constructor, "(enteredDate)" will parse that date string and conver it into a date object
        };
        console.log(expenseData);
    };
    return (
        <form onSubmit={submitHandler}>
            <div className='new-expense__controls'>
                <div className='new-expense__control'>
                    <label>Title</label>
                    <input type='text' onChange={titleChangeHandler} />
                </div>

                <div className='new-expense__control'>
                    <label>Amount</label>
                    <input type='number' min="0.01" step="0.01" onChange={amountChangeHandler} />
                </div>

                <div className='new-expense__control'>
                    <label>Date</label>
                    <input type='number' min="2019-01-01" max="2022-12-31" onChange={dateChangeHandler} />
                </div>
            </div>

            <div className='new-expense__actions'>
                <button type="submit" >Add Expense </button>
            </div>
        </form>
    );
};

export default ExpenseForm;




