import Chart from '../chart/Chart'; 

const ExpensesChart = props => {

const chartDataPoints = [
    {label: 'Jan', values: 0}, 
    {label: 'Feb', values: 0},
    {label: 'Feb', values: 0},
    {label: 'Mar', values: 0},
    {label: 'Apr', values: 0},
    {label: 'May', values: 0},
    {label: 'Jun', values: 0},
    {label: 'Jul', values: 0},
    {label: 'Aug', values: 0},
    {label: 'Sep', values: 0},
    {label: 'Oct', values: 0},
    {label: 'Nov', values: 0},
    {label: 'Dec', values: 0},
]; 

for(const expense of props.expense){ // here, we get the month value of each expense and add it to the month above
    const expenseMonth = expense.date.getMonth(); //starting at 0 +. January => 0
chartDataPoints[expenseMonth].values += expense.amount; 
}

    return <Chart dataPoints = {chartDataPoints}/>

};

export default ExpensesChart;
